package com.gurushi.rest.util;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;public enum SpringContext {		INSTANCE;	private ApplicationContext ctx;		private SpringContext() {}	private ApplicationContext init(){		if(ctx == null){			ctx = new ClassPathXmlApplicationContext("application-context.xml");		}		return ctx;	}	public <T extends Object> T getBean(String beanName, Class<T> beanClass){		ApplicationContext ctx = init();		return ctx.getBean(beanName, beanClass);	}		public void set(ApplicationContext context){		ctx = context;	}}